/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect } from "react";
import FsLightbox from "fslightbox";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";

function ProductOne(props) {
  const { t } = useTranslation();
  const { picture, title, price } = props;

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  const openLightbox = () => {
    new FsLightbox().open();
  };

  const options = {
    sources: [picture], // Lightbox içinde görüntülenecek medya kaynağı
    onOpen: () => {
      // Lightbox açıldığında çalışacak işlev
    },
    onClose: () => {
      // Lightbox kapatıldığında çalışacak işlev
    },
    buttons: {
      showDownloadButton: false, // İndirme düğmesini gösterme
      showThumbnailsButton: false, // Miniatürler düğmesini gösterme
    },
  };

  useEffect(() => {
    const lightbox = new FsLightbox(options);

    return () => {
      console(log)
      lightbox.detach();
    };
  }, [options]);

  return (
    <div className="product__item">
      <div className="product__item__img">
        <img src={picture} alt={title} onClick={openLightbox} />
      </div>
      <div className="product__item__content">
        <div className="product__item__ratings">
          <span className="fa fa-star"></span>
          <span className="fa fa-star"></span>
          <span className="fa fa-star"></span>
          <span className="fa fa-star"></span>
          <span className="fa fa-star"></span>
        </div>
        <h4 className="product__item__title">
          <Link to="/ProductDetails" onClick={scrollToTop}>
            {title}
          </Link>
        </h4>
        <Link
          to="/ProductDetails"
          className="nisoz-btn"
          onClick={scrollToTop}
        >
          <span className="nisoz-btn__shape"></span>
          <span className="nisoz-btn__shape"></span>
          <span className="nisoz-btn__shape"></span>
          <span className="nisoz-btn__shape"></span>
          <span className="nisoz-btn__text">{price}</span>
        </Link>
      </div>
    </div>
  );
}

export default ProductOne;
