// Contactus.js
import React from "react";
import emailjs from "emailjs-com";
import { useTranslation } from "react-i18next";

function Contactus() {
  const { t } = useTranslation();

  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_lp35s39",
        "template_giqb4o4",
        e.target,
        "7LseggvnGoIcgGHia"
      )
      .then(
        (result) => {
          console.log(result.text);
          alert(t("Email başarıyla gönderildi!"));
        },
        (error) => {
          console.log(error.text);
          alert(t("Email gönderilirken bir hata oluştu!"));
        }
      );
    e.target.reset();
  }

  return (
    <>
      <section className="contact-one contact-page">
        <div className="container wow fadeInUp" data-wow-delay="300ms">
          <div className="section-title text-center">
            <div className="section-title__triangle">
              <img src="assets/images/favicons/icon.ico" alt="icon" />
            </div>
            <h5 className="section-title__tagline">{t("BİZİMLE İLETİŞİM GEÇİN")}</h5>
            <h2 className="section-title__title">{t("İLETİŞİM")}</h2>
          </div>

          <div className="container-md">
            {/* Diğer kısım aynı şekilde devam eder */}
          </div>

          <div className="contact-one__form-box container-md mt-40">
            <form
              onSubmit={sendEmail}
              className="contact-one__form contact-form-validated"
              noValidate="noValidate"
            >
              <div className="row">
                {/* Diğer kısım aynı şekilde devam eder */}
              </div>
            </form>
          </div>

          <div className="container-md mt-4">
            {/* Google Harita kısmı aynı şekilde devam eder */}
          </div>
        </div>
      </section>
    </>
  );
}

export default Contactus;
