/* eslint-disable no-unused-vars */
import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { SRLWrapper } from "simple-react-lightbox";

function ProductOne(props) {
  const { t } = useTranslation();
  const { picture, title, price } = props;

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  const options = {
    settings: {
      overlayColor: "rgba(0, 0, 0, 0.9)", // Overlay rengi
      autoplaySpeed: 3000, // Otomatik oynatma hızı
      transitionSpeed: 1000, // Geçiş hızı
      disablePanzoom: false, // Yakınlaştırma devre dışı bırak
      closeButton: true, // Kapatma düğmesini göster
    },
  };
  
  return (
    <>
      <div className="product__item">
        <div className="product__item__img">
          <img src={picture} alt={title} />
          <div className="product__item__btn">
            <SRLWrapper options={options}>
              <a href="assets/images/blog/11-2.jpg" className="img-popup">
                <i className="fas fa-eye"></i>
              </a>
            </SRLWrapper>
          </div>
          <div className="product__item__content">
            <div className="product__item__ratings">
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
            </div>
            <h4 className="product__item__title">
              <Link as={Link} to="/ProductDetails" onClick={scrollToTop}>
                {title}
              </Link>
            </h4>
            <Link
              as={Link}
              to="/ProductDetails"
              className="nisoz-btn"
              onClick={scrollToTop}
            >
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__text">{price}</span>
            </Link>
          </div>
        </div>
      </div>
    </>
  );
}

export default ProductOne;
